import React from 'react'
import Stack from '@mui/material/Stack';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Avatar from '@mui/material/Avatar';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useRef,useState } from 'react';
import { checkValidate } from './Utils/validate';
import { Link, useNavigate, Outlet } from 'react-router-dom';


const SignUp = () => {
  const[user,setUser] = useState({
    firstname:"",
    lastname:"",
    email:"",
    password:""
  });
  // const [email, setEmail] = useState("");
  const [errorMessage, setErrorMessage] = useState(null);
  const [displayName, setDisplayName] = useState({first:"",last:""});
  const navigate = useNavigate();
  const message = checkValidate(user.email);

  const handleChange =(e) =>{
    
    setUser({...user, [e.target.name]:e.target.value});
  }

  const handleSubmitError=(e)=>{
    e.preventDefault();
    console.log(message);
    setErrorMessage(message);
    if(!message && user){
         navigate("/");
         setDisplayName(user.firstname,user.lastname);
    }
    else{
      navigate("/");
    }
   }

  //  const handleSubmit = () =>{
  //   if(errorMessage){
  //     navigate("/");
  //   }
  //   else{
  //     navigate("/signup");
  //   }
  //  }
 
  
  return (
    <Container>
        <Box>
            <Typography className="" variant='h5'>Sign Up</Typography>
            <Box>
            {/* <form autoComplete='off' onSubmit={handleSubmitError}> */}
             <Stack spacing={2}>
                    <TextField required id="firstname" name="firstname" label="First Name"></TextField>
                    <TextField required id="lastname" name="lastname" label="Last Name"></TextField>
                    <TextField  required value={user.email} onChange={handleChange} id="email" name="email" label="Email Id" helperText={errorMessage}></TextField>

                    <TextField required id="password" name="password" type="password" label="Password"></TextField>
             </Stack>
             <Button onClick={handleSubmitError} variant="contained">Sign Up</Button>
             {/* <Link to = "/signup">Sign Up</Link> */}
             {/* </form> */}
            </Box>
        </Box>
      </Container>
  );
 }
export default SignUp;
