import React from 'react'
import Stack from '@mui/material/Stack';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Avatar from '@mui/material/Avatar';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useRef,useState } from 'react';
import { checkValidate } from './Utils/validate';


const SignUp = () => {
  const [email, setEmail] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);

  const handleChange =() =>{
    setEmail((e)=>{[e.target.name];e.target.value});
  }

  const handleSubmitError=()=>{
    const message = checkValidate(email.current.value);
    console.log(message);
    setErrorMessage(message);
  }
  
  return (
    <Container>
        <Box>
            <Typography className="" variant='h5'>Sign Up</Typography>
            <Box>
             <Stack spacing={2}>
                    <TextField required id="firstname" name="firstname" label="First Name"></TextField>
                    <TextField required id="lastname" name="lastname" label="Last Name"></TextField>
                    <TextField onChange={()=>{setEmail()}} required id="email" name="email" label="Email Id" helperText={errorMessage}></TextField>
                    {/* <p>{errorMessage}</p> */}
                    <TextField required id="password" name="password" type="password" label="Password"></TextField>
             </Stack>
             <Button variant="contained" onClick={handleSubmitError}>SIGN UP</Button>
            </Box>
        </Box>
      </Container>
  )
}

export default SignUp
