import React from 'react'
import Stack from '@mui/material/Stack';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Avatar from '@mui/material/Avatar';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useRef,useState } from 'react';
import { checkValidate } from './Utils/validate';
import { Link, useNavigate, Outlet } from 'react-router-dom';


const SignUp = () => {
  const[user,setUser] = useState({
    firstname:"",
    lastname:"",
    email:"",
    password:""
  });
  // const [email, setEmail] = useState("");
  const [errorMessage, setErrorMessage] = useState();
  const [displayFirstName, setDisplayFirstName] = useState(null);
  const [displayLastName, setDisplayLastName] = useState(null);
  const navigate = useNavigate();
  const message = checkValidate(user.email,user.password);

  const handleChange =(e) =>{
    setUser({...user, [e.target.name]:e.target.value});
  }

  const handleSubmitError=(e)=>{
    e.preventDefault();
    console.log(message);
    setErrorMessage(message);
    console.log(user);
    if(!message && user){
         navigate("/"); 
         setDisplayFirstName(user.firstname);
         setDisplayLastName(user.lastname);
    }

   }
  
  return (
    <Container className='h-screen w-full bg-gradient-to-b from-zinc-200 via-teal-100 to-gray-100'>
        <Box className="w-3/12 h-3/12 mx-auto py-44 bg-rose-200">
            <Typography className="" variant='h5'>Sign Up</Typography>
            <Box>
            <form autoComplete='off' onSubmit={handleSubmitError}>
             <Stack spacing={2}>
                    <TextField required value={user.firstname} onChange={handleChange} id="firstname" name="firstname" label="First Name"></TextField>
                    <TextField required value={user.lastname} onChange={handleChange} id="lastname" name="lastname" label="Last Name"></TextField>
                    <TextField  required value={user.email} onChange={handleChange} id="email" name="email" label="Email Id"></TextField>
                    <TextField required value={user.password} onChange={handleChange} id="password" name="password" type="password" label="Password"></TextField>
                    <p>{errorMessage}</p>
             </Stack>
             <Button type="submit" variant="contained">Login</Button>
             </form>
            {user && <p>{displayFirstName} {displayLastName}</p>}
            </Box>
        </Box>
      </Container>
  );
 }
export default SignUp;
