import React from 'react'
import Stack from '@mui/material/Stack';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Avatar from '@mui/material/Avatar';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useRef,useState } from 'react';
import { checkValidate } from './Utils/validate';
import { Link, useNavigate, Outlet } from 'react-router-dom';


const SignUp = () => {
  const[user,setUser] = useState({
    firstname:"",
    lastname:"",
    email:"",
    password:""
  });
  // const [email, setEmail] = useState("");
  const [errorMessage, setErrorMessage] = useState(null);
  const navigate = useNavigate();

  const handleChange =(e) =>{
    
    setUser({...user, [e.target.name]:e.target.value});
  }

  const handleSubmitError=()=>{
    const message = checkValidate(user.email);
    console.log(message);
    setErrorMessage(message);
    // if(user && !errorMessage){
    //   navigate("/");
   }
  
  return (
    <Container>
        <Box>
            <Typography className="" variant='h5'>Sign Up</Typography>
            <Box>
             <Stack spacing={2}>
                    <TextField required id="firstname" name="firstname" label="First Name"></TextField>
                    <TextField required id="lastname" name="lastname" label="Last Name"></TextField>
                    <TextField value={user.email} onChange={handleChange} required id="email" name="email" label="Email Id" helperText={errorMessage}></TextField>

                    <TextField required id="password" name="password" type="password" label="Password"></TextField>
             </Stack>
             <Button variant="contained"><Link to="/signup" onClick={handleSubmitError}>Sign Up</Link></Button>
            </Box>
        </Box>
      </Container>
  );
 }
export default SignUp;
