import React from 'react'
import Stack from '@mui/material/Stack';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Avatar from '@mui/material/Avatar';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useRef,useState } from 'react';
import { checkValidate } from './Utils/validate';


const SignUp = () => {
  const email = useRef(null);
  const [errorMessage, setErrorMessage] = useState(null);
  const message = checkValidate(email.current.value);
  return (
    <Container>
        <Box>
            <Typography className="" variant='h5'>Sign Up</Typography>
            <Box>
             <Stack spacing={2}>
                    <TextField required id="firstname" name="firstname" label="First Name"></TextField>
                    <TextField required id="lastname" name="lastname" label="Last Name"></TextField>
                    <TextField ref={email} required id="emailId" name="emailId" label="Email Id"></TextField>
                    <p>{errorMessage}</p>
                    <TextField required id="password" name="password" type="password" label="Password"></TextField>
             </Stack>
             <Button variant="contained">SIGN UP</Button>
            </Box>
        </Box>
      </Container>
  )
}

export default SignUp
